mapper.select:
SELECT id, name, description, sort, code, type, status, create_time
FROM db_club.sys_role

    name LIKE CONCAT('%', #{condition.name}, '%')
    OR code LIKE CONCAT('%', #{condition.code}, '%')
    OR `status`= #{condition.status}
    OR create_time
        >= #{condition.startTime}

    OR create_time BETWEEN
        #{condition.startTime} AND #{condition.endTime}
